import React, { Component } from "react";
import "../App.css";
import { connect } from "react-redux";
import * as thunks from "../action/thunk";
import Profile from "./Profile";


class Commits extends Component {
    constructor() {
        super()

    }


    addComment = async () => {
        const blogId = this.props.match.params.blogid;
        await this.props.addComment(blogId);

    }


    render() {
        const { like, likes } = this.props;


        return (
            <div className="Commits">
                <Profile />

                <div>
                    {/* <label> Add Commit</label> */}
                    <textarea > </textarea>
                    <button onclick={() => this.addComment()}> Comment </button>
                </div>


            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        authorBlogs: state.profile.authorsBlogs,
    };
}

function mapDispatchToProps(dispatch) {
    return {
        addComment: (blogId) => dispatch(thunks.addComment(blogId))
    };
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Commits);
