import React, { Component } from "react";
import "../App.css";
import { connect } from "react-redux";
import * as thunks from "../action/thunk";
import Profile from "./Profile";
import { reduxForm, Field } from "redux-form";
import history from '../history';

class Comments extends Component {
    constructor() {
        super()
    }

    getAllComments = async (blogId) => {
        await this.props.getAllComments(blogId);

    }

    handleSubmit = (data) => {
        const blogId = this.props.match.params.blogid;
        const blogInfo = { id: blogId, text: data.text };
        this.props.addComment(blogInfo);
        this.getAllComments(blogId);

        // history.push(`/viewcomments/${blogId}`);
    }

    render() {
        const { handleSubmit } = this.props;


        return (
            <div className="Commits">
                <Profile />
                <form onSubmit={handleSubmit(this.handleSubmit)}>
                    <Field type="text" name="text" value="name" component="input" />
                    <input type="submit" />

                </form>

            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        authorBlogs: state.profile.authorsBlogs,
    };
}

function mapDispatchToProps(dispatch) {
    return {
        addComment: (blogId) => dispatch(thunks.addComment(blogId)),
        getAllComments: (blogId) =>
            dispatch(thunks.getAllComments(blogId)),

    };
}



const CommentFormConfig = reduxForm({
    form: "addComment"
})(Comments);

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(CommentFormConfig);
