import React, { Component } from "react";
import "../App.css";
import { reduxForm, Field } from "redux-form";
import { connect } from "react-redux";
import * as thunks from "../action/thunk";


class FormRegister extends Component {
  constructor() {
    super();

    this.state = {
      isSuccess: false
    };
  }

  handleSubmit = async data => {
    await this.props.signInAction(data, this.props.history);
  };


  sleep = ms => new Promise(resolve => setTimeout(resolve, ms));

  asyncValidate = (values /*, dispatch */) => {
    return this.sleep(1000).then(() => { // simulate server latency
      if (['john', 'paul', 'george', 'ringo'].includes(values.username)) {
        throw { username: 'That username is taken' };
      }
    });
  };
  renderField = (
    { input, label, type, meta: { asyncValidating, touched, error } },
  ) => (
      <div>
        <label>{label}</label>
        <div className={asyncValidating ? 'async-validating' : ''}>
          <input {...input} type={type} placeholder={label} />
          {touched && error && <span>{error}</span>}
        </div>
      </div>
    );

  render() {
    const { handleSubmit } = this.props;
    console.log(this.state.isSuccess);
    console.log()

    return (
      <div className="Form">
        <h1> Register Form </h1>
        <form onSubmit={handleSubmit(this.handleSubmit)}>
          {/* <label> email </label> */}
          <Field type="text" label="Email" name="email" component={this.renderField} />
          <label> password </label>
          <Field type="password" label="Password" name="password" component="input" />
          <label> author </label>
          <Field type="author" label="Author" name="author" component="input" />
          <input type="submit" />
        </form>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    signInAction: (data, history) =>
      dispatch(thunks.signInAction(data, history))
  };
}




const registerUser = reduxForm({
  form: "registerUser",
  asyncValidate
})(FormRegister);

export default connect(
  null,
  mapDispatchToProps
)(registerUser);
