import React from "react";


const textArea = ({
    input,
    label,
    type,
    meta: { touched, error }
}) => {
    return (
        <div>
            <label> {label}</label>
            <TextArea>
                <textarea {...input} type={type} placeholder={label} />
                {touched && error && <span>{this.props.errors.email}</span>}
            </TextArea>
        </div>
    )
}


export default textArea;
