import "../App.css";
import { connect } from "react-redux";
import * as thunks from "../action/thunk";

class UserInfo extends Component {
    componentDidMount() {
        this.getBlogsForAuthor();

    }

    render() {

        return (
            <div className="UserInfo">
                <h1> Profile </h1>
                <label> Author Name</label>

                    <p> text </p>
                    <span> Date </span>

            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        authorBlogs: state.user.authorBlogs
    };
}

function mapDispatchToProps(dispatch) {
    return {
        getBlogsForAuthor: () => dispatch(thunks.getBlogsForAuthor())
    };
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(UserInfo);
